package study.inflearn.hash.problem04_04;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.params.provider.Arguments.of;

import java.util.stream.Stream;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

class ApplicationTest {

	@ParameterizedTest
	@MethodSource("problem04_04_params")
	void solutionTest(String s, String t, int expect) {
		Application app = new Application();

		int answer = app.solution(s, t);

		assertEquals(expect, answer);
	}

	private static Stream<Arguments> problem04_04_params() {
		return Stream.of(
			of("bacaAacba", "abc", 3),
			of("AbcDecbDc", "bcD", 3),
			of("ABCEFGHIJKLMNPQRSTUVWXYZabcaefghijklmnopqrstuvwxyz", "abc", 2),
			of("WetabtqtqEFqGHIJKLtabeWPQRSTUVWetabdgXYabteWcdefghijklmnopqrabtWestuwxyz", "Wetab",
				5),
			of(""
				, "WetabtqtqEFqGHIJKLtabeWPQRSTUVWetabdgXYabteWcdefghijklmnopqrabt", 180)
		);
	}
}